export const fetchAPIWrapper = async (selectedDate) => {
    if (typeof window.fetchAPI === 'function') {
        return await window.fetchAPI(selectedDate);
    } else {
        // Provide default behavior when the API function is not available
        console.warn('fetchAPI is not available');
        return [];
    }
};

export const submitAPIWrapper = async (formData) => {
    if (typeof window.submitAPI === 'function') {
        return await window.submitAPI(formData);
    } else {
        // Provide default behavior when the API function is not available
        console.warn('submitAPI is not available');
        return false;
    }
};
