import React, {useEffect, useState} from 'react';
import {fetchAPI} from '../api.js';
import './styles/BookingForm.css';

function BookingForm({ formData, onFormChange, submitForm }) {
    const today = new Date();
    const yyyy = today.getFullYear();
    let mm = today.getMonth() + 1; // Months start at 0!
    let dd = today.getDate();

    if (dd < 10) dd = '0' + dd;
    if (mm < 10) mm = '0' + mm;

    formData.date = yyyy + '/' + mm + '/' + dd;
    const [availableTimes, setAvailableTimes] = useState([]);


    const updateTimes = async (selectedDate) => {
        const times = fetchAPI(selectedDate);
        setAvailableTimes(times);
    };

    useEffect(() => {
        updateTimes(formData.date).then(r => console.log(r));
    }, [formData.date]);

    const handleSubmit = (event) => {
        event.preventDefault();
        submitForm(formData);
    };


    const handleChange = (event) => {
        const target = event.target;
        const name = target.id;
        const value = target.type === 'date' ? target.value : target.value;

        onFormChange({
            ...formData,
            [name]: value,
        });

        if (event.target.id === 'res-date') {
            const selectedDate = new Date(event.target.value);
            updateTimes(selectedDate).then(r => console.log(r));
        }
    };

    const handleTimeClick = (time) => {
        onFormChange({
            ...formData,
            time,
        });
    };

    const [isFormValid, setIsFormValid] = useState(false);

    // eslint-disable-next-line react-hooks/exhaustive-deps
    const validateForm = () => {
        const hasDate = !!formData.date;
        const hasTime = !!formData.time;
        const hasGuests = formData.guests > 0;

        setIsFormValid(hasDate && hasTime && hasGuests);
    };

    useEffect(() => {
        validateForm();
    }, [formData, validateForm]);

    return (
        <form onSubmit={handleSubmit} style={{ gap: '20px' }}>
            <label htmlFor="res-date">Choose date</label>
            <input
                type="date"
                id="res-date"
                defaultValue={formData.date}
                onChange={handleChange}
                min={yyyy + '-' + mm + '-' + dd}
            />

            <label>Choose time</label>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                {availableTimes.map((availableTime) => (
                    <button
                        className="time"
                        key={availableTime}
                        type="button"
                        onClick={() => handleTimeClick(availableTime)}
                        style={{
                            backgroundColor: formData.time === availableTime ? '#f4ce14' : 'black',
                            color: formData.time === availableTime ? 'black' : 'white',
                        }}
                    >
                        {availableTime}
                    </button>
                ))}
            </div>
            <label htmlFor="guests">Number of guests</label>
            <input
                className="slider"
                type="range"
                min="1"
                max="10"
                step="1"
                id="guests"
                value={formData.guests}
                onChange={handleChange}
            />
            <p className="guests">{formData.guests} guests</p>
            <label htmlFor="occasion">Occasion</label>
            <select
                id="occasion"
                value={formData.occasion}
                onChange={handleChange}
            >
                <option value="occasion" disabled>
                    Select an occasion
                </option>
                <option value="Birthday">Birthday</option>
                <option value="Engagement">Engagement</option>
                <option value="Anniversary">Anniversary</option>
            </select>
            <input
                type="submit"
                value="Make Your reservation"
                disabled={!isFormValid}
            />
        </form>
    );
}

export default BookingForm;
